mapper.select:


		select * from(  		
			select rownum as rn, reviewId, productId, memberId, content, rating, regDate
			from REVIEW
			where rownum <= #{pageNum} * #{amount}  and bookId = #{bookId}	
			order by regDate desc  	
  			)
  		where rn > (#{pageNum} -1) * #{amount}


select count(*)
from REVIEW
where productId = #{productId}
